	for (int i = 0; i < num; i++)
	{
		GLfloat ptH[4] = { vertices0[i][0], vertices0[i][1], vertices0[i][2], 1 };//齐次坐标
		GLfloat res[4] = {0, 0, 0, 0};

		for (int j = 0; j < 4; j++)
			for (int k = 0; k < 4; k++)
				res[j] += (ptH[k] * mA.p[k][j]);

		vertices1[i][0] = res[0] / res[3];
		vertices1[i][1] = res[1] / res[3];
	}
}

void Polygon(int clr, int a, int b, int c, int d)
{
	glColor3fv(colors[clr]);
	if (flag == 1)
		glBegin(GL_LINE_LOOP);
	else
		glBegin(GL_POLYGON);

	glVertex2fv(vertices1[a]);
	glVertex2fv(vertices1[b]);
	glVertex2fv(vertices1[c]);
	glVertex2fv(vertices1[d]);
	glEnd();
}

void ColorCube(void)
{
	Polygon(0, 0, 3, 2, 1);
	Polygon(1, 2, 3, 7, 6);
	Polygon(2, 0, 4, 7, 3);
	Polygon(3, 1, 2, 6, 5);
	Polygon(4, 4, 5, 6, 7);
	Polygon(5, 0, 1, 5, 4);
	//Polygon(0, 0, 3, 2, 1);
}

void myDisplay()
{
	glClear(GL_COLOR_BUFFER_BIT);
	glColor3f(1.0f, 0.0f, 0.0f);

	InitParameter();

	Project(8, vertices0, vertices1);
	ColorCube();

	glutSwapBuffers();
}

void Init()
{
	glClearColor(0.0, 0.0, 0.0, 0.0);
	glShadeModel(GL_FLAT);

	printf("Hello Cube!\n");
}

void Reshape(int w, int h)
{
	glViewport(0, 0, (GLsizei)w, (GLsizei)h);
	glMatrixMode(GL_PROJECTION);
	glLoadIdentity();
	glOrtho(-5, 5, -5, 5, -10, 10);
}

void keyboard(unsigned char key, int x, int y)
{
	switch (key)
	{
	case '1':
		flag = 1;
		glutPostRedisplay();
		break;
	case '2':
		flag = 2;
		glutPostRedisplay();
		break;
	default:
		break;
	}
}

int main(int argc, char *argv[])
{
	glutInit(&argc, argv);
	glutInitDisplayMode(GLUT_RGB | GLUT_DOUBLE);
	glutInitWindowPosition(100, 100);
	glutInitWindowSize(600, 600);
	glutCreateWindow("Hello World!");

